import {
  AppBar,
  Toolbar,
  Typography,
  makeStyles,
  Button,
 } from "@material-ui/core";
import React from "react";
import { Link as RouterLink } from "react-router-dom";
import "./Footer.css";


const footerData = [
  {
    label: "Home",
    href: "/",
  },
  {
    label: "About",
    href: "/AboutMe",
  },
  {
    label: "Blog",
    href: "/AllPosts",
  }


];

const useStyles = makeStyles(() => ({
  header: {
    backgroundColor: "#400CCC",
    paddingRight: "79px",
    paddingLeft: "118px",
  },
  logo: {
    fontFamily: "Work Sans, sans-serif",
    fontWeight: 600,
    color: "#FFFEFE",
    textAlign: "left",
  },
  menuButton: {
      fontFamily: "Open Sans, sans-serif",
      fontWeight: 700,
      size: "18px",
      marginLeft: "38px",
   },
   toolbar: {
     display: "flex",
     justifyContent: "space-between",
   },
}));

export default function Footer () {
  const { footer, menuButton } = useStyles();

  const displayDesktop = () => {
    return (
      <Toolbar>
        <div>{getMenuButtons()}</div>
      </Toolbar>
   )
  };

const getMenuButtons = () => {
 return footerData.map(({ label, href }) => {
   return (
     <Button
       {...{
         key: label,
         color: "inherit",
         to: href,
         component: RouterLink,
         className: menuButton
       }}
     >
       {label}
     </Button>
   );
 });
};

return (
  <header>
    <AppBar className={footer}>{displayDesktop()}</AppBar>
  </header>
);
}
