import { Link } from 'react-router-dom';

const AllPosts = ({blogs, title}) => {

    return (
        <div className="all-posts">
            <h2>{title}</h2>
            {blogs.map(blog => (
                <div className="blog-preview" key={blog.id}>
                    <Link to={`/blog/${blog.id}`}>
                        <h2>{blog.title}</h2>
                        <p>Written by {blog.author}</p>
                    </Link>
                </div>
            ))}
        </div>
     );
}

export default AllPosts;
